import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import session from "express-session";
import { 
  insertUserSchema, 
  insertLoanApplicationSchema, 
  insertDsaPartnerSchema, 
  insertLeadSchema, 
  insertContactQuerySchema 
} from "@shared/schema";

// Session middleware
function setupSession(app: Express) {
  app.use(session({
    secret: process.env.SESSION_SECRET || 'jsmf-secret-key-2024',
    resave: false,
    saveUninitialized: false,
    cookie: {
      secure: false,
      maxAge: 24 * 60 * 60 * 1000 // 24 hours
    }
  }));
}

// Auth middleware
function requireAuth(req: any, res: any, next: any) {
  if (!req.session.userId) {
    return res.status(401).json({ message: "Unauthorized" });
  }
  next();
}

function requireRole(role: string) {
  return (req: any, res: any, next: any) => {
    if (!req.session.userId || req.session.userRole !== role) {
      return res.status(403).json({ message: "Forbidden" });
    }
    next();
  };
}

declare module 'express-session' {
  interface SessionData {
    userId: string;
    userRole: string;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  setupSession(app);

  // Auth routes
  app.post('/api/auth/login', async (req, res) => {
    try {
      const { username, password } = req.body;
      const user = await storage.authenticateUser(username, password);

      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.userId = user.id;
      req.session.userRole = user.role;

      res.json({ 
        user: { 
          id: user.id, 
          username: user.username, 
          role: user.role, 
          fullName: user.fullName,
          email: user.email 
        } 
      });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "Login failed" });
    }
  });

  app.post('/api/auth/logout', (req, res) => {
    req.session.destroy(() => {
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get('/api/auth/user', requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json({ 
        id: user.id, 
        username: user.username, 
        role: user.role, 
        fullName: user.fullName,
        email: user.email 
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to get user" });
    }
  });

  // User registration
  app.post('/api/users/register', async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByUsername(userData.username);
      if (existingUser) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const existingEmail = await storage.getUserByEmail(userData.email);
      if (existingEmail) {
        return res.status(400).json({ message: "Email already registered" });
      }

      const user = await storage.createUser(userData);
      res.status(201).json({ 
        user: { 
          id: user.id, 
          username: user.username, 
          role: user.role, 
          fullName: user.fullName,
          email: user.email 
        } 
      });
    } catch (error) {
      console.error("Registration error:", error);
      res.status(400).json({ message: "Registration failed" });
    }
  });

  // Loan application routes
  app.post('/api/loan-applications', requireAuth, async (req, res) => {
    try {
      const applicationData = insertLoanApplicationSchema.parse(req.body);
      const application = await storage.createLoanApplication({
        ...applicationData,
        userId: req.session.userId!
      });

      // Create a lead for this application
      const user = await storage.getUser(req.session.userId!);
      if (user) {
        await storage.createLead({
          name: user.fullName,
          mobileNumber: user.mobileNumber,
          email: user.email,
          loanType: applicationData.loanType,
          amount: applicationData.amount || "0",
          city: user.city || "Bhopal",
          source: "application"
        });
      }

      res.status(201).json(application);
    } catch (error) {
      console.error("Application creation error:", error);
      res.status(400).json({ message: "Failed to create application" });
    }
  });

  app.get('/api/loan-applications', requireAuth, async (req, res) => {
    try {
      let applications;
      if (req.session.userRole === 'admin') {
        applications = await storage.getLoanApplications();
      } else if (req.session.userRole === 'dsa') {
        applications = await storage.getLoanApplicationsByDsa(req.session.userId!);
      } else {
        applications = await storage.getLoanApplicationsByUser(req.session.userId!);
      }
      res.json(applications);
    } catch (error) {
      res.status(500).json({ message: "Failed to get applications" });
    }
  });

  app.patch('/api/loan-applications/:id', requireRole('admin'), async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const application = await storage.updateLoanApplication(id, updates);
      res.json(application);
    } catch (error) {
      res.status(400).json({ message: "Failed to update application" });
    }
  });

  // DSA partner routes
  app.post('/api/dsa-partners', async (req, res) => {
    try {
      const { userData, partnerData } = req.body;

      // Create user first
      const userInput = { ...userData, role: "dsa" };
      const parsedUserData = insertUserSchema.parse(userInput);
      const user = await storage.createUser(parsedUserData);

      // Create DSA partner profile
      const parsedPartnerData = insertDsaPartnerSchema.parse(partnerData);
      const partner = await storage.createDsaPartner({
        ...parsedPartnerData,
        userId: user.id
      });

      res.status(201).json({ user, partner });
    } catch (error) {
      console.error("DSA registration error:", error);
      res.status(400).json({ message: "DSA registration failed" });
    }
  });

  app.get('/api/dsa-partners', requireRole('admin'), async (req, res) => {
    try {
      const partners = await storage.getDsaPartners();
      res.json(partners);
    } catch (error) {
      res.status(500).json({ message: "Failed to get DSA partners" });
    }
  });

  app.get('/api/dsa-partners/profile', requireRole('dsa'), async (req, res) => {
    try {
      const partner = await storage.getDsaPartnerByUserId(req.session.userId!);
      res.json(partner);
    } catch (error) {
      res.status(500).json({ message: "Failed to get DSA profile" });
    }
  });

  // Lead management routes
  app.post('/api/leads', async (req, res) => {
    try {
      const leadData = insertLeadSchema.parse(req.body);
      const lead = await storage.createLead(leadData);
      res.status(201).json(lead);
    } catch (error) {
      console.error("Lead creation error:", error);
      res.status(400).json({ message: "Failed to create lead" });
    }
  });

  app.get('/api/leads', requireAuth, async (req, res) => {
    try {
      let leads;
      if (req.session.userRole === 'admin') {
        leads = await storage.getLeads();
      } else if (req.session.userRole === 'dsa') {
        leads = await storage.getLeadsByDsa(req.session.userId!);
      } else {
        return res.status(403).json({ message: "Forbidden" });
      }
      res.json(leads);
    } catch (error) {
      res.status(500).json({ message: "Failed to get leads" });
    }
  });

  app.patch('/api/leads/:id/assign', requireRole('admin'), async (req, res) => {
    try {
      const { id } = req.params;
      const { dsaId } = req.body;
      const lead = await storage.assignLeadToDsa(id, dsaId);
      res.json(lead);
    } catch (error) {
      res.status(400).json({ message: "Failed to assign lead" });
    }
  });

  app.patch('/api/leads/:id', requireAuth, async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;
      const lead = await storage.updateLead(id, updates);
      res.json(lead);
    } catch (error) {
      res.status(400).json({ message: "Failed to update lead" });
    }
  });

  // Contact queries
  app.post('/api/contact-queries', async (req, res) => {
    try {
      const queryData = insertContactQuerySchema.parse(req.body);
      const query = await storage.createContactQuery(queryData);
      res.status(201).json(query);
    } catch (error) {
      console.error("Contact query error:", error);
      res.status(400).json({ message: "Failed to submit query" });
    }
  });

  app.get('/api/contact-queries', requireRole('admin'), async (req, res) => {
    try {
      const queries = await storage.getContactQueries();
      res.json(queries);
    } catch (error) {
      res.status(500).json({ message: "Failed to get contact queries" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  loanApplications, 
  dsaPartners, 
  leads, 
  contactQueries,
  type User, 
  type InsertUser,
  type LoanApplication,
  type InsertLoanApplication,
  type DsaPartner,
  type InsertDsaPartner,
  type Lead,
  type InsertLead,
  type ContactQuery,
  type InsertContactQuery
} from "@shared/schema";
import { randomUUID } from "crypto";
import bcrypt from "bcrypt";
import { db } from "./db";
import { eq } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, user: Partial<User>): Promise<User>;
  authenticateUser(username: string, password: string): Promise<User | null>;

  // Loan application operations
  createLoanApplication(application: InsertLoanApplication & { userId: string }): Promise<LoanApplication>;
  getLoanApplications(): Promise<LoanApplication[]>;
  getLoanApplicationsByUser(userId: string): Promise<LoanApplication[]>;
  getLoanApplicationsByDsa(dsaId: string): Promise<LoanApplication[]>;
  updateLoanApplication(id: string, updates: Partial<LoanApplication>): Promise<LoanApplication>;

  // DSA partner operations
  createDsaPartner(partner: InsertDsaPartner & { userId: string }): Promise<DsaPartner>;
  getDsaPartners(): Promise<DsaPartner[]>;
  getDsaPartnerByUserId(userId: string): Promise<DsaPartner | undefined>;
  updateDsaPartner(id: string, updates: Partial<DsaPartner>): Promise<DsaPartner>;

  // Lead operations
  createLead(lead: InsertLead): Promise<Lead>;
  getLeads(): Promise<Lead[]>;
  getLeadsByDsa(dsaId: string): Promise<Lead[]>;
  assignLeadToDsa(leadId: string, dsaId: string): Promise<Lead>;
  updateLead(id: string, updates: Partial<Lead>): Promise<Lead>;

  // Contact query operations
  createContactQuery(query: InsertContactQuery): Promise<ContactQuery>;
  getContactQueries(): Promise<ContactQuery[]>;
  updateContactQuery(id: string, updates: Partial<ContactQuery>): Promise<ContactQuery>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private loanApplications: Map<string, LoanApplication> = new Map();
  private dsaPartners: Map<string, DsaPartner> = new Map();
  private leads: Map<string, Lead> = new Map();
  private contactQueries: Map<string, ContactQuery> = new Map();

  constructor() {
    this.initializeDefaultUsers();
  }

  private async initializeDefaultUsers() {
    // Create admin user
    const adminId = randomUUID();
    const hashedPassword = await bcrypt.hash("Harsh@9131", 10);
    this.users.set(adminId, {
      id: adminId,
      username: "harsh@jsmf.in",
      email: "harsh@jsmf.in",
      password: hashedPassword,
      role: "admin",
      fullName: "Harsh Kumar",
      mobileNumber: "+91 91626 207918",
      city: "Bhopal",
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    });
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);
    const user: User = {
      ...insertUser,
      id,
      password: hashedPassword,
      role: insertUser.role || "user",
      city: insertUser.city || null,
      isActive: true,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User> {
    const user = this.users.get(id);
    if (!user) throw new Error("User not found");

    const updatedUser = { ...user, ...updates, updatedAt: new Date() };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async authenticateUser(username: string, password: string): Promise<User | null> {
    const user = await this.getUserByUsername(username);
    if (!user) return null;

    const isValid = await bcrypt.compare(password, user.password);
    return isValid ? user : null;
  }

  // Loan application operations
  async createLoanApplication(application: InsertLoanApplication & { userId: string }): Promise<LoanApplication> {
    const id = randomUUID();
    const loanApp: LoanApplication = {
      ...application,
      id,
      userId: application.userId,
      amount: application.amount || null,
      tenure: application.tenure || null,
      monthlyIncome: application.monthlyIncome || null,
      employmentType: application.employmentType || null,
      purpose: application.purpose || null,
      interestRate: "7.5",
      status: "pending",
      assignedDsaId: null,
      documents: null,
      remarks: null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.loanApplications.set(id, loanApp);
    return loanApp;
  }

  async getLoanApplications(): Promise<LoanApplication[]> {
    return Array.from(this.loanApplications.values());
  }

  async getLoanApplicationsByUser(userId: string): Promise<LoanApplication[]> {
    return Array.from(this.loanApplications.values()).filter(app => app.userId === userId);
  }

  async getLoanApplicationsByDsa(dsaId: string): Promise<LoanApplication[]> {
    return Array.from(this.loanApplications.values()).filter(app => app.assignedDsaId === dsaId);
  }

  async updateLoanApplication(id: string, updates: Partial<LoanApplication>): Promise<LoanApplication> {
    const application = this.loanApplications.get(id);
    if (!application) throw new Error("Application not found");

    const updatedApp = { ...application, ...updates, updatedAt: new Date() };
    this.loanApplications.set(id, updatedApp);
    return updatedApp;
  }

  // DSA partner operations
  async createDsaPartner(partner: InsertDsaPartner & { userId: string }): Promise<DsaPartner> {
    const id = randomUUID();
    const dsaPartner: DsaPartner = {
      ...partner,
      id,
      userId: partner.userId,
      experience: partner.experience || null,
      background: partner.background || null,
      commissionRate: "2.0",
      totalEarnings: "0",
      totalLeads: 0,
      successfulLeads: 0,
      kycStatus: "pending",
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.dsaPartners.set(id, dsaPartner);
    return dsaPartner;
  }

  async getDsaPartners(): Promise<DsaPartner[]> {
    return Array.from(this.dsaPartners.values());
  }

  async getDsaPartnerByUserId(userId: string): Promise<DsaPartner | undefined> {
    return Array.from(this.dsaPartners.values()).find(partner => partner.userId === userId);
  }

  async updateDsaPartner(id: string, updates: Partial<DsaPartner>): Promise<DsaPartner> {
    const partner = this.dsaPartners.get(id);
    if (!partner) throw new Error("DSA partner not found");

    const updatedPartner = { ...partner, ...updates, updatedAt: new Date() };
    this.dsaPartners.set(id, updatedPartner);
    return updatedPartner;
  }

  // Lead operations
  async createLead(lead: InsertLead): Promise<Lead> {
    const id = randomUUID();
    const newLead: Lead = {
      ...lead,
      id,
      email: lead.email || null,
      amount: lead.amount || null,
      city: lead.city || null,
      source: lead.source || "website",
      status: "new",
      assignedDsaId: null,
      assignedAt: null,
      convertedAt: null,
      remarks: null,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.leads.set(id, newLead);
    return newLead;
  }

  async getLeads(): Promise<Lead[]> {
    return Array.from(this.leads.values());
  }

  async getLeadsByDsa(dsaId: string): Promise<Lead[]> {
    return Array.from(this.leads.values()).filter(lead => lead.assignedDsaId === dsaId);
  }

  async assignLeadToDsa(leadId: string, dsaId: string): Promise<Lead> {
    const lead = this.leads.get(leadId);
    if (!lead) throw new Error("Lead not found");

    const updatedLead = { 
      ...lead, 
      assignedDsaId: dsaId, 
      assignedAt: new Date(),
      updatedAt: new Date() 
    };
    this.leads.set(leadId, updatedLead);
    return updatedLead;
  }

  async updateLead(id: string, updates: Partial<Lead>): Promise<Lead> {
    const lead = this.leads.get(id);
    if (!lead) throw new Error("Lead not found");

    const updatedLead = { ...lead, ...updates, updatedAt: new Date() };
    this.leads.set(id, updatedLead);
    return updatedLead;
  }

  // Contact query operations
  async createContactQuery(query: InsertContactQuery): Promise<ContactQuery> {
    const id = randomUUID();
    const contactQuery: ContactQuery = {
      ...query,
      id,
      email: query.email || null,
      loanType: query.loanType || null,
      status: "new",
      createdAt: new Date(),
    };
    this.contactQueries.set(id, contactQuery);
    return contactQuery;
  }

  async getContactQueries(): Promise<ContactQuery[]> {
    return Array.from(this.contactQueries.values());
  }

  async updateContactQuery(id: string, updates: Partial<ContactQuery>): Promise<ContactQuery> {
    const query = this.contactQueries.get(id);
    if (!query) throw new Error("Contact query not found");

    const updatedQuery = { ...query, ...updates };
    this.contactQueries.set(id, updatedQuery);
    return updatedQuery;
  }
}

// Database storage implementation
export class DbStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.email, email)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const hashedPassword = await bcrypt.hash(insertUser.password, 10);
    const userData = {
      ...insertUser,
      password: hashedPassword,
      role: insertUser.role || "user",
    };
    const result = await db.insert(users).values(userData).returning();
    return result[0];
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User> {
    const result = await db.update(users).set({...updates, updatedAt: new Date()}).where(eq(users.id, id)).returning();
    if (!result[0]) throw new Error("User not found");
    return result[0];
  }

  async authenticateUser(username: string, password: string): Promise<User | null> {
    const user = await this.getUserByUsername(username);
    if (!user) return null;

    const isValid = await bcrypt.compare(password, user.password);
    return isValid ? user : null;
  }

  // Loan application operations
  async createLoanApplication(application: InsertLoanApplication & { userId: string }): Promise<LoanApplication> {
    const result = await db.insert(loanApplications).values(application).returning();
    return result[0];
  }

  async getLoanApplications(): Promise<LoanApplication[]> {
    return await db.select().from(loanApplications);
  }

  async getLoanApplicationsByUser(userId: string): Promise<LoanApplication[]> {
    return await db.select().from(loanApplications).where(eq(loanApplications.userId, userId));
  }

  async getLoanApplicationsByDsa(dsaId: string): Promise<LoanApplication[]> {
    return await db.select().from(loanApplications).where(eq(loanApplications.assignedDsaId, dsaId));
  }

  async updateLoanApplication(id: string, updates: Partial<LoanApplication>): Promise<LoanApplication> {
    const result = await db.update(loanApplications).set({...updates, updatedAt: new Date()}).where(eq(loanApplications.id, id)).returning();
    if (!result[0]) throw new Error("Application not found");
    return result[0];
  }

  // DSA partner operations
  async createDsaPartner(partner: InsertDsaPartner & { userId: string }): Promise<DsaPartner> {
    const result = await db.insert(dsaPartners).values(partner).returning();
    return result[0];
  }

  async getDsaPartners(): Promise<DsaPartner[]> {
    return await db.select().from(dsaPartners);
  }

  async getDsaPartnerByUserId(userId: string): Promise<DsaPartner | undefined> {
    const result = await db.select().from(dsaPartners).where(eq(dsaPartners.userId, userId)).limit(1);
    return result[0];
  }

  async updateDsaPartner(id: string, updates: Partial<DsaPartner>): Promise<DsaPartner> {
    const result = await db.update(dsaPartners).set({...updates, updatedAt: new Date()}).where(eq(dsaPartners.id, id)).returning();
    if (!result[0]) throw new Error("DSA partner not found");
    return result[0];
  }

  // Lead operations
  async createLead(lead: InsertLead): Promise<Lead> {
    const result = await db.insert(leads).values(lead).returning();
    return result[0];
  }

  async getLeads(): Promise<Lead[]> {
    return await db.select().from(leads);
  }

  async getLeadsByDsa(dsaId: string): Promise<Lead[]> {
    return await db.select().from(leads).where(eq(leads.assignedDsaId, dsaId));
  }

  async assignLeadToDsa(leadId: string, dsaId: string): Promise<Lead> {
    const result = await db.update(leads).set({ assignedDsaId: dsaId, assignedAt: new Date(), updatedAt: new Date() }).where(eq(leads.id, leadId)).returning();
    if (!result[0]) throw new Error("Lead not found");
    return result[0];
  }

  async updateLead(id: string, updates: Partial<Lead>): Promise<Lead> {
    const result = await db.update(leads).set({...updates, updatedAt: new Date()}).where(eq(leads.id, id)).returning();
    if (!result[0]) throw new Error("Lead not found");
    return result[0];
  }

  // Contact query operations
  async createContactQuery(query: InsertContactQuery): Promise<ContactQuery> {
    const result = await db.insert(contactQueries).values(query).returning();
    return result[0];
  }

  async getContactQueries(): Promise<ContactQuery[]> {
    return await db.select().from(contactQueries);
  }

  async updateContactQuery(id: string, updates: Partial<ContactQuery>): Promise<ContactQuery> {
    const result = await db.update(contactQueries).set(updates).where(eq(contactQueries.id, id)).returning();
    if (!result[0]) throw new Error("Contact query not found");
    return result[0];
  }
}

export const storage = new DbStorage();

