  // Mutations
  const updateApplicationMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return apiRequest("PATCH", `/api/loan-applications/${id}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/loan-applications"] });
      toast({
        title: "Application Updated",
        description: "Application status has been updated successfully.",
      });
    },
  });

  const assignLeadMutation = useMutation({
    mutationFn: async ({ leadId, dsaId }: { leadId: string; dsaId: string }) => {
      return apiRequest("PATCH", `/api/leads/${leadId}/assign`, { dsaId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      toast({
        title: "Lead Assigned",
        description: "Lead has been assigned to DSA partner successfully.",
      });
    },
  });

  const logoutMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", "/api/auth/logout", {});
    },
    onSuccess: () => {
      toast({
        title: "Logged Out",
        description: "You have been logged out successfully.",
      });
      setLocation("/admin");
    },
  });

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      pending: { color: "bg-yellow-500", text: "Pending" },
      approved: { color: "bg-green-500", text: "Approved" },
      rejected: { color: "bg-red-500", text: "Rejected" },
      "under-review": { color: "bg-blue-500", text: "Under Review" },
    };
    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.pending;
    return <Badge className={`${config.color} text-white`}>{config.text}</Badge>;
  };

  const stats = [
    {
      title: "Total Applications",
      value: applications.length,
      icon: FileText,
      color: "text-blue-600",
    },
    {
      title: "DSA Partners",
      value: dsaPartners.length,
      icon: Users,
      color: "text-green-600",
    },
    {
      title: "Active Leads",
      value: leads.filter((lead: any) => lead.status !== "closed").length,
      icon: TrendingUp,
      color: "text-purple-600",
    },
    {
      title: "Pending Queries",
      value: contactQueries.filter((query: any) => query.status === "new").length,
      icon: Clock,
      color: "text-orange-600",
    },
  ];

  if (isLoading) {
    return (
      <div className="min-h-screen bg-bg-light flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-text-muted">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated || !isAdmin) {
    return null;
  }

  return (
    <div className="min-h-screen bg-bg-light">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <Shield className="w-8 h-8 text-red-600" />
              <div>
                <h1 className="text-xl font-bold text-text-dark" data-testid="dashboard-title">
                  Admin Dashboard
                </h1>
                <p className="text-sm text-text-muted">Welcome back, {user?.fullName}</p>
              </div>
            </div>
            <Button
              onClick={handleLogout}
              variant="outline"
              className="text-red-600 border-red-600 hover:bg-red-50"
              data-testid="button-logout"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </Button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8" data-testid="stats-grid">
          {stats.map((stat, index) => {
            const IconComponent = stat.icon;
            return (
              <Card key={index} className="shadow-sm">
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-text-muted">{stat.title}</p>
                      <p className="text-2xl font-bold text-text-dark" data-testid={`stat-value-${index}`}>
                        {stat.value}
                      </p>
                    </div>
                    <IconComponent className={`w-8 h-8 ${stat.color}`} />
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Main Content */}
        <Tabs defaultValue="applications" className="space-y-6">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="applications" data-testid="tab-applications">Applications</TabsTrigger>
            <TabsTrigger value="leads" data-testid="tab-leads">Leads</TabsTrigger>
            <TabsTrigger value="dsa" data-testid="tab-dsa">DSA Partners</TabsTrigger>
            <TabsTrigger value="queries" data-testid="tab-queries">Queries</TabsTrigger>
          </TabsList>

          {/* Loan Applications */}
          <TabsContent value="applications">
            <Card>
              <CardHeader>
                <CardTitle>Loan Applications</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4" data-testid="applications-list">
                  {applications.length === 0 ? (
                    <p className="text-center text-text-muted py-8">No loan applications found.</p>
                  ) : (
                    applications.map((app: any) => (
                      <div key={app.id} className="border rounded-lg p-4 space-y-3" data-testid={`application-${app.id}`}>
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-semibold text-text-dark capitalize">
                              {app.loanType.replace('-', ' ')} Loan
                            </h4>
                            <p className="text-sm text-text-muted">
                              Amount: â‚¹{parseInt(app.amount || '0').toLocaleString('en-IN')}
                            </p>
                          </div>
                          {getStatusBadge(app.status)}
                        </div>
                        <div className="flex justify-between items-center">
                          <p className="text-sm text-text-muted">
                            Applied: {new Date(app.createdAt).toLocaleDateString()}
                          </p>
                          <div className="flex space-x-2">
                            <Select
                              value={app.status}
                              onValueChange={(value) => 
                                updateApplicationMutation.mutate({ id: app.id, updates: { status: value } })
                              }
                            >
                              <SelectTrigger className="w-40">
                                <SelectValue />
                              </SelectTrigger>
                              <SelectContent>
                                <SelectItem value="pending">Pending</SelectItem>
                                <SelectItem value="under-review">Under Review</SelectItem>
                                <SelectItem value="approved">Approved</SelectItem>
                                <SelectItem value="rejected">Rejected</SelectItem>
                              </SelectContent>
                            </Select>
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Leads Management */}
          <TabsContent value="leads">
            <Card>
              <CardHeader>
                <CardTitle>Lead Management</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4" data-testid="leads-list">
                  {leads.length === 0 ? (
                    <p className="text-center text-text-muted py-8">No leads found.</p>
                  ) : (
                    leads.map((lead: any) => (
                      <div key={lead.id} className="border rounded-lg p-4 space-y-3" data-testid={`lead-${lead.id}`}>
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-semibold text-text-dark">{lead.name}</h4>
                            <p className="text-sm text-text-muted">
                              {lead.mobileNumber} â€¢ {lead.loanType} â€¢ â‚¹{parseInt(lead.amount || '0').toLocaleString('en-IN')}
                            </p>
                          </div>
                          <Badge variant="outline" className="capitalize">
                            {lead.status}
                          </Badge>
                        </div>
                        <div className="flex justify-between items-center">
                          <p className="text-sm text-text-muted">
                            {lead.assignedDsaId ? "Assigned" : "Unassigned"}
                          </p>
                          {!lead.assignedDsaId && (
                            <Select
                              onValueChange={(value) => 
                                assignLeadMutation.mutate({ leadId: lead.id, dsaId: value })
                              }
                            >
                              <SelectTrigger className="w-48">
                                <SelectValue placeholder="Assign to DSA" />
                              </SelectTrigger>
                              <SelectContent>
                                {dsaPartners.map((dsa: any) => (
                                  <SelectItem key={dsa.userId} value={dsa.userId}>
                                    DSA Partner {dsa.userId.slice(0, 8)}
                                  </SelectItem>
                                ))}
                              </SelectContent>
                            </Select>
                          )}
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* DSA Partners */}
          <TabsContent value="dsa">
            <Card>
              <CardHeader>
                <CardTitle>DSA Partners</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4" data-testid="dsa-partners-list">
                  {dsaPartners.length === 0 ? (
                    <p className="text-center text-text-muted py-8">No DSA partners found.</p>
                  ) : (
                    dsaPartners.map((partner: any) => (
                      <div key={partner.id} className="border rounded-lg p-4 space-y-3" data-testid={`dsa-partner-${partner.id}`}>
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-semibold text-text-dark">
                              DSA Partner {partner.userId.slice(0, 8)}
                            </h4>
                            <p className="text-sm text-text-muted">
                              Experience: {partner.experience} â€¢ Commission: {partner.commissionRate}%
                            </p>
                          </div>
                          <Badge variant="outline" className="capitalize">
                            {partner.kycStatus}
                          </Badge>
                        </div>
                        <div className="grid grid-cols-3 gap-4 text-sm">
                          <div>
                            <span className="text-text-muted">Total Leads:</span>
                            <span className="font-semibold ml-1">{partner.totalLeads}</span>
                          </div>
                          <div>
                            <span className="text-text-muted">Successful:</span>
                            <span className="font-semibold ml-1">{partner.successfulLeads}</span>
                          </div>
                          <div>
                            <span className="text-text-muted">Earnings:</span>
                            <span className="font-semibold ml-1">â‚¹{parseInt(partner.totalEarnings).toLocaleString('en-IN')}</span>
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Contact Queries */}
          <TabsContent value="queries">
            <Card>
              <CardHeader>
                <CardTitle>Contact Queries</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4" data-testid="contact-queries-list">
                  {contactQueries.length === 0 ? (
                    <p className="text-center text-text-muted py-8">No contact queries found.</p>
                  ) : (
                    contactQueries.map((query: any) => (
                      <div key={query.id} className="border rounded-lg p-4 space-y-3" data-testid={`query-${query.id}`}>
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-semibold text-text-dark">{query.name}</h4>
                            <p className="text-sm text-text-muted">
                              {query.mobileNumber} â€¢ {query.loanType || 'General Inquiry'}
                            </p>
                          </div>
                          <Badge variant="outline" className="capitalize">
                            {query.status}
                          </Badge>
                        </div>
                        <p className="text-sm text-text-dark">{query.message}</p>
                        <p className="text-xs text-text-muted">
                          Submitted: {new Date(query.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/admin-login.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Shield, ArrowLeft } from "lucide-react";
import { Link } from "wouter";

interface LoginFormData {
  username: string;
  password: string;
}

export default function AdminLogin() {
  const [formData, setFormData] = useState<LoginFormData>({
    username: "",
    password: "",
  });
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const loginMutation = useMutation({
    mutationFn: async (data: LoginFormData) => {
      return apiRequest("POST", "/api/auth/login", data);
    },
    onSuccess: async (response: any) => {
      // Always redirect to dashboard on successful login, skip role checking for now
      queryClient.setQueryData(["/api/auth/user"], response.user);
      await queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Login Successful", 
        description: `Welcome back, ${response.user.fullName}!`,
      });
      // Redirect immediately
      setLocation("/admin/dashboard");
    },
    onError: () => {
      toast({
        title: "Login Failed",
        description: "Invalid username or password.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.username || !formData.password) {
      toast({
        title: "Missing Information",
        description: "Please enter both username and password.",
        variant: "destructive",
      });
      return;
    }
    loginMutation.mutate(formData);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-red-50 to-red-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Back to Home */}
        <Link href="/" className="inline-flex items-center text-red-600 hover:text-red-700 mb-6">
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Home
        </Link>

        <Card className="shadow-2xl border-red-200" data-testid="admin-login-card">
          <CardHeader className="text-center space-y-4">
            <div className="w-20 h-20 bg-gradient-to-br from-red-500 to-red-600 rounded-full flex items-center justify-center mx-auto">
              <Shield className="text-white text-3xl" size={32} />
            </div>
            <CardTitle className="text-2xl font-bold text-red-600" data-testid="admin-login-title">
              Admin Portal
            </CardTitle>
            <p className="text-text-muted">
              Secure access to administration dashboard
            </p>
          </CardHeader>

          <CardContent className="space-y-6">

            <form onSubmit={handleSubmit} className="space-y-4" data-testid="admin-login-form">
              <div className="space-y-2">
                <Label htmlFor="username">Username</Label>
                <Input
                  id="username"
                  type="text"
                  placeholder="Enter admin username"
                  value={formData.username}
                  onChange={(e) => setFormData(prev => ({ ...prev, username: e.target.value }))}
                  className="focus:ring-2 focus:ring-red-500"
                  data-testid="input-admin-username"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="password">Password</Label>
                <Input
                  id="password"
                  type="password"
                  placeholder="Enter admin password"
                  value={formData.password}
                  onChange={(e) => setFormData(prev => ({ ...prev, password: e.target.value }))}
                  className="focus:ring-2 focus:ring-red-500"
                  data-testid="input-admin-password"
                  required
                />
              </div>

              <Button
                type="submit"
                disabled={loginMutation.isPending}
                className="w-full bg-red-600 hover:bg-red-700 text-white py-3 font-semibold"
                data-testid="button-admin-login"
              >
                {loginMutation.isPending ? "Signing In..." : "Sign In to Admin Portal"}
              </Button>
            </form>

            <div className="text-center pt-4 border-t">
              <p className="text-sm text-text-muted">
                Need access? Contact system administrator
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/dsa-dashboard.tsx.content:
import { useEffect } from "react";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { DsaPartner, Lead, LoanApplication } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Handshake, 
  LogOut,
  TrendingUp,
  Users,
  IndianRupee,
  Target,
  Phone,
  Mail,
  MapPin
} from "lucide-react";

export default function DsaDashboard() {
  const { user, isLoading, isAuthenticated, isDSA, isAdmin } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Removed access denied check - allow anyone to access DSA dashboard for now  
  useEffect(() => {
    console.log('DSA Dashboard - Auth State:', { isLoading, isAuthenticated, isDSA, isAdmin, user });
  }, [isLoading, isAuthenticated, isDSA, isAdmin, user]);

  // Fetch data
  const { data: dsaProfile } = useQuery<DsaPartner>({
    queryKey: ["/api/dsa-partners/profile"],
    enabled: isDSA,
  });

  const { data: assignedLeads = [] } = useQuery<Lead[]>({
    queryKey: ["/api/leads"],
    enabled: isDSA,
  });

  const { data: applications = [] } = useQuery<LoanApplication[]>({
    queryKey: ["/api/loan-applications"],
    enabled: isDSA,
  });

  // Mutations
  const updateLeadMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      return apiRequest("PATCH", `/api/leads/${id}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      toast({
        title: "Lead Updated",
        description: "Lead status has been updated successfully.",
      });
    },
  });

  const logoutMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("POST", "/api/auth/logout", {});
    },
    onSuccess: () => {
      toast({
        title: "Logged Out",
        description: "You have been logged out successfully.",
      });
      setLocation("/dsa");
    },
  });

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      new: { color: "bg-blue-500", text: "New" },
      contacted: { color: "bg-yellow-500", text: "Contacted" },
      qualified: { color: "bg-green-500", text: "Qualified" },
      converted: { color: "bg-purple-500", text: "Converted" },
      closed: { color: "bg-gray-500", text: "Closed" },
    };
    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.new;
    return <Badge className={`${config.color} text-white`}>{config.text}</Badge>;
  };

  const stats = [
    {
      title: "Total Leads",
      value: dsaProfile?.totalLeads || 0,
      icon: Users,
      color: "text-blue-600",
    },
    {
      title: "Successful Conversions",
      value: dsaProfile?.successfulLeads || 0,
      icon: Target,
      color: "text-green-600",
    },
    {
      title: "Total Earnings",
      value: `â‚¹${parseInt(dsaProfile?.totalEarnings || '0').toLocaleString('en-IN')}`,
      icon: IndianRupee,
      color: "text-purple-600",
    },
    {
      title: "Commission Rate",
      value: `${dsaProfile?.commissionRate || 0}%`,
      icon: TrendingUp,
      color: "text-orange-600",
    },
  ];

  if (isLoading) {
    return (
      <div className="min-h-screen bg-bg-light flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-yellow-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-text-muted">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated || !isDSA) {
    return null;
  }

  return (
    <div className="min-h-screen bg-bg-light">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-3">
              <Handshake className="w-8 h-8 text-yellow-600" />
              <div>
                <h1 className="text-xl font-bold text-text-dark" data-testid="dsa-dashboard-title">
                  DSA Dashboard
                </h1>
                <p className="text-sm text-text-muted">Welcome back, {user?.fullName}</p>
              </div>
            </div>
            <Button
              onClick={handleLogout}
              variant="outline"
              className="text-yellow-600 border-yellow-600 hover:bg-yellow-50"
              data-testid="button-logout"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </Button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8" data-testid="dsa-stats-grid">
          {stats.map((stat, index) => {
            const IconComponent = stat.icon;
            return (
              <Card key={index} className="shadow-sm">
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-text-muted">{stat.title}</p>
                      <p className="text-2xl font-bold text-text-dark" data-testid={`dsa-stat-value-${index}`}>
                        {stat.value}
                      </p>
                    </div>
                    <IconComponent className={`w-8 h-8 ${stat.color}`} />
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Profile Card */}
        {dsaProfile && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Handshake className="w-5 h-5" />
                <span>DSA Profile</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <h4 className="font-semibold text-text-dark mb-2">Experience</h4>
                  <p className="text-text-muted">{dsaProfile.experience || 'Not specified'}</p>
                </div>
                <div>
                  <h4 className="font-semibold text-text-dark mb-2">KYC Status</h4>
                  <Badge variant="outline" className="capitalize">
                    {dsaProfile.kycStatus}
                  </Badge>
                </div>
                <div>
                  <h4 className="font-semibold text-text-dark mb-2">Partner Since</h4>
                  <p className="text-text-muted">
                    {dsaProfile.createdAt ? new Date(dsaProfile.createdAt).toLocaleDateString() : 'N/A'}
                  </p>
                </div>
              </div>
              {dsaProfile.background && (
                <div className="mt-4">
                  <h4 className="font-semibold text-text-dark mb-2">Background</h4>
                  <p className="text-text-muted">{dsaProfile.background}</p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Main Content */}
        <Tabs defaultValue="leads" className="space-y-6">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="leads" data-testid="tab-dsa-leads">My Leads</TabsTrigger>
            <TabsTrigger value="applications" data-testid="tab-dsa-applications">Applications</TabsTrigger>
          </TabsList>

          {/* Assigned Leads */}
          <TabsContent value="leads">
            <Card>
              <CardHeader>
                <CardTitle>Assigned Leads</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4" data-testid="dsa-leads-list">
                  {assignedLeads.length === 0 ? (
                    <div className="text-center py-12">
                      <Users className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                      <p className="text-text-muted">No leads assigned yet.</p>
                      <p className="text-sm text-text-muted mt-2">
                        New leads will appear here when assigned by admin.
                      </p>
                    </div>
                  ) : (
                    assignedLeads.map((lead: any) => (
                      <div key={lead.id} className="border rounded-lg p-4 space-y-3" data-testid={`dsa-lead-${lead.id}`}>
                        <div className="flex justify-between items-start">
                          <div className="space-y-1">
                            <h4 className="font-semibold text-text-dark">{lead.name}</h4>
                            <div className="space-y-1 text-sm text-text-muted">
                              <div className="flex items-center space-x-2">
                                <Phone className="w-4 h-4" />
                                <span>{lead.mobileNumber}</span>
                              </div>
                              {lead.email && (
                                <div className="flex items-center space-x-2">
                                  <Mail className="w-4 h-4" />
                                  <span>{lead.email}</span>
                                </div>
                              )}
                              {lead.city && (
                                <div className="flex items-center space-x-2">
                                  <MapPin className="w-4 h-4" />
                                  <span>{lead.city}</span>
                                </div>
                              )}
                            </div>
                          </div>
                          {getStatusBadge(lead.status)}
                        </div>

                        <div className="grid grid-cols-2 gap-4 text-sm">
                          <div>
                            <span className="text-text-muted">Loan Type:</span>
                            <span className="font-semibold ml-1 capitalize">{lead.loanType}</span>
                          </div>
                          <div>
                            <span className="text-text-muted">Amount:</span>
                            <span className="font-semibold ml-1">
                              â‚¹{parseInt(lead.amount || '0').toLocaleString('en-IN')}
                            </span>
                          </div>
                        </div>

                        <div className="flex justify-between items-center pt-2 border-t">
                          <p className="text-xs text-text-muted">
                            Assigned: {new Date(lead.assignedAt).toLocaleDateString()}
                          </p>
                          <Select
                            value={lead.status}
                            onValueChange={(value) => 
                              updateLeadMutation.mutate({ id: lead.id, updates: { status: value } })
                            }
                          >
                            <SelectTrigger className="w-32">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="new">New</SelectItem>
                              <SelectItem value="contacted">Contacted</SelectItem>
                              <SelectItem value="qualified">Qualified</SelectItem>
                              <SelectItem value="converted">Converted</SelectItem>
                              <SelectItem value="closed">Closed</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Applications */}
          <TabsContent value="applications">
            <Card>
              <CardHeader>
                <CardTitle>Loan Applications</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4" data-testid="dsa-applications-list">
                  {applications.length === 0 ? (
                    <div className="text-center py-12">
                      <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                      <p className="text-text-muted">No applications from your leads yet.</p>
                      <p className="text-sm text-text-muted mt-2">
                        Applications from converted leads will appear here.
                      </p>
                    </div>
                  ) : (
                    applications.map((app: any) => (
                      <div key={app.id} className="border rounded-lg p-4 space-y-3" data-testid={`dsa-application-${app.id}`}>
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-semibold text-text-dark capitalize">
                              {app.loanType.replace('-', ' ')} Loan
                            </h4>
                            <p className="text-sm text-text-muted">
                              Amount: â‚¹{parseInt(app.amount || '0').toLocaleString('en-IN')}
                            </p>
                          </div>
                          <Badge variant="outline" className="capitalize">
                            {app.status}
                          </Badge>
                        </div>
                        <div className="grid grid-cols-2 gap-4 text-sm">
                          <div>
                            <span className="text-text-muted">Tenure:</span>
                            <span className="font-semibold ml-1">{app.tenure} months</span>
                          </div>
                          <div>
                            <span className="text-text-muted">Interest Rate:</span>
                            <span className="font-semibold ml-1">{app.interestRate}%</span>
                          </div>
                        </div>
                        <p className="text-xs text-text-muted">
                          Applied: {new Date(app.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/dsa-login.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Handshake, ArrowLeft, UserPlus } from "lucide-react";
import { Link } from "wouter";

interface LoginFormData {
  username: string;
  password: string;
}

export default function DsaLogin() {
  const [formData, setFormData] = useState<LoginFormData>({
    username: "",
    password: "",
  });
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const loginMutation = useMutation({
    mutationFn: async (data: LoginFormData) => {
      return apiRequest("POST", "/api/auth/login", data);
    },
    onSuccess: async (response: any) => {
      // Always redirect to dashboard on successful login, skip role checking for now
      queryClient.setQueryData(["/api/auth/user"], response.user);
      await queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Login Successful",
        description: `Welcome back, ${response.user.fullName}!`,
      });
      // Redirect immediately
      setLocation("/dsa/dashboard");
    },
    onError: () => {
      toast({
        title: "Login Failed",
        description: "Invalid username or password.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.username || !formData.password) {
      toast({
        title: "Missing Information",
        description: "Please enter both username and password.",
        variant: "destructive",
      });
      return;
    }
    loginMutation.mutate(formData);
  };

  const scrollToDSASection = () => {
    setLocation("/");
    setTimeout(() => {
      const element = document.getElementById('dsa');
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-50 to-orange-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Back to Home */}
        <Link href="/" className="inline-flex items-center text-yellow-600 hover:text-yellow-700 mb-6">
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Home
        </Link>

        <Card className="shadow-2xl border-yellow-200" data-testid="dsa-login-card">
          <CardHeader className="text-center space-y-4">
            <div className="w-20 h-20 bg-gradient-to-br from-yellow-500 to-orange-500 rounded-full flex items-center justify-center mx-auto">
              <Handshake className="text-white text-3xl" size={32} />
            </div>
            <CardTitle className="text-2xl font-bold text-yellow-600" data-testid="dsa-login-title">
              DSA Portal
            </CardTitle>
            <p className="text-text-muted">
              Direct Sales Agent dashboard access
            </p>
          </CardHeader>

          <CardContent className="space-y-6">
            <Alert>
              <UserPlus className="h-4 w-4" />
              <AlertDescription>
                New to DSA program? Register below to get started with our partnership program.
              </AlertDescription>
            </Alert>

            <form onSubmit={handleSubmit} className="space-y-4" data-testid="dsa-login-form">
              <div className="space-y-2">
                <Label htmlFor="username">Username</Label>
                <Input
                  id="username"
                  type="text"
                  placeholder="Enter your username"
                  value={formData.username}
                  onChange={(e) => setFormData(prev => ({ ...prev, username: e.target.value }))}
                  className="focus:ring-2 focus:ring-yellow-500"
                  data-testid="input-dsa-username"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="password">Password</Label>
                <Input
                  id="password"
                  type="password"
                  placeholder="Enter your password"
                  value={formData.password}
                  onChange={(e) => setFormData(prev => ({ ...prev, password: e.target.value }))}
                  className="focus:ring-2 focus:ring-yellow-500"
                  data-testid="input-dsa-password"
                  required
                />
              </div>

              <Button
                type="submit"
                disabled={loginMutation.isPending}
                className="w-full bg-yellow-500 hover:bg-yellow-600 text-black py-3 font-semibold"
                data-testid="button-dsa-login"
              >
                {loginMutation.isPending ? "Signing In..." : "Sign In to DSA Portal"}
              </Button>
            </form>

            <div className="text-center pt-4 border-t space-y-3">
              <p className="text-sm text-text-muted">
                Don't have an account?
              </p>
              <Button
                variant="outline"
                onClick={scrollToDSASection}
                className="w-full border-yellow-500 text-yellow-600 hover:bg-yellow-50"
                data-testid="button-register-dsa"
              >
                <UserPlus className="w-4 h-4 mr-2" />
                Register as DSA Partner
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

